import React, { Component } from "react";
import SpotifyWebApi from "spotify-web-api-js";

const spotifyApi = new SpotifyWebApi();

class Home extends Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      getAlbum: {
        artists: "",
        genres: "",
        id: "",
        label: "",
        name: "",
        release_date: "",
        traks: "",
        images: {
          height: 300,
          witdh: 300,
        },
      },
    };
    if (params.access_token) {
      spotifyApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getSavedAlbum() {
    spotifyApi.getMySavedAlbums().then((response) => {
      this.setState({
        getAlbum: {
          artists: response.item.artists.name,
          genres: response.item.genres,
          id: response.item.id,
          label: response.item.label,
          name: response.item.name,
          release_date: response.item.release_date,
          traks: response.item.traks,
          images: response.item.image,
        },
      });
    });
  }

  render() {
    return (
      <div>
        <a href="http://localhost:8888" className="Spotify-Login">
          Login With Spotify
        </a>
        <div>Artist: {this.state.getAlbum.artists}</div>
        <button onClick={() => this.getSavedAlbum()}>Check Saved Albums</button>
      </div>
    );
  }
}

export default Home;
